import { useRef, useState } from "react";

const Checkout = (props) => {
  const cityInputRef = useRef();
  const streetInputRef = useRef();
  const houseInputRef = useRef();
  const postalInputRef = useRef();

  const [cityHasError, setCityHasError] = useState(false);
  const [streetHasError, setStreetHasError] = useState(false);
  const [houseHasError, setHouseHasError] = useState(false);
  const [postalHasError, setPostalHasError] = useState(false);


  const isEmpty = (value) => {
    if (value === "") {
      return true;
    }
    return false;
  };

  const hasPostalFormat = (value) => {
    if (/^\d+$/.test(value) && value.length === 5) {
      return true;
    }
    return false;
  };

  let formIsValid = false;

  const submitHandler = (event) => {
    event.preventDefault();

    setCityHasError(isEmpty(cityInputRef.current.value));
    setStreetHasError(isEmpty(streetInputRef.current.value));
    setHouseHasError(isEmpty(houseInputRef.current.value));
    setPostalHasError(!hasPostalFormat(postalInputRef.current.value));

    formIsValid = !(cityHasError || streetHasError || houseHasError || postalHasError);
    if(formIsValid) {
        console.log('Form Has Error Block');
        return;
    }

    const clientData = {
      city: cityInputRef.current.value,
      Street: streetInputRef.current.value,
      house: houseInputRef.current.value,
      postal: postalInputRef.current.value,
    };
    props.onOrder(clientData);
  };

  return (
    <form onSubmit={submitHandler}>
      <div className="control-group">
        <div>
          <label htmlFor="city">City</label>
          <input type="text" id="city" ref={cityInputRef}></input>
          {cityHasError && <p>This field is required</p>}
        </div>
        <div>
          <label htmlFor="street">Street</label>
          <input type="text" id="street" ref={streetInputRef}></input>
          {streetHasError && <p>This field is required</p>}
        </div>
        <div>
          <label htmlFor="house">House</label>
          <input type="text" id="house" ref={houseInputRef}></input>
          {houseHasError && <p>This field is required</p>}
        </div>
        <div>
          <label htmlFor="postal">Postal code</label>
          <input type="text" id="postal" ref={postalInputRef}></input>
          {postalHasError && <p>5 digits is required</p>}
        </div>
      </div>
      <div className="form-actions">
        <button type="button" onClick={props.onClose}>
          Cancel
        </button>
        <button>Submit</button>
      </div>
    </form>
  );
};

export default Checkout;
