import { useRef, useState } from "react";

const Checkout = (props) => {
  const cityInputRef = useRef();
  const streetInputRef = useRef();
  const houseInputRef = useRef();
  const postalInputRef = useRef();

  const [cityIsValid, setCityIsValid] = useState(false);
  const [streetIsValid, setStreetIsValid] = useState(false);
  const [houseIsValid, setHouseIsValid] = useState(false);
  const [postalIsValid, setPostalIsValid] = useState(false);

  const [formValidity, setFormValidity] = useState({
    city: true,
    street: true,
    house: true,
    postal: true,
  });

  const isEmpty = (value) => {
    if (value.trim() === "") {
      return true;
    }
    return false;
  };

  const hasPostalFormat = (value) => {
    if (/^\d+$/.test(value.trim()) && value.trim().length === 5) {
      return true;
    }
    return false;
  };

  let formIsValid = false;

  const submitHandler = (event) => {
    event.preventDefault();

    cityIsValid = !isEmpty(cityInputRef.current.value);
    streetIsValid = !isEmpty(streetInputRef.current.value);
    houseIsValid = !isEmpty(houseInputRef.current.value);
    postalIsValid = hasPostalFormat(postalInputRef.current.value);

    formIsValid = cityIsValid && streetIsValid && houseIsValid && postalIsValid;
    if (!formIsValid) {
      console.log("Form Has Error Block");
      return;
    }

    const clientData = {
      city: cityInputRef.current.value,
      Street: streetInputRef.current.value,
      house: houseInputRef.current.value,
      postal: postalInputRef.current.value,
    };
    props.onOrder(clientData);
  };

  return (
    <form onSubmit={submitHandler}>
      <div className="control-group">
        <div>
          <label htmlFor="city">City</label>
          <input type="text" id="city" ref={cityInputRef}></input>
          {!cityIsValid && <p>This field is required</p>}
        </div>
        <div>
          <label htmlFor="street">Street</label>
          <input type="text" id="street" ref={streetInputRef}></input>
          {!streetIsValid && <p>This field is required</p>}
        </div>
        <div>
          <label htmlFor="house">House</label>
          <input type="text" id="house" ref={houseInputRef}></input>
          {!houseIsValid && <p>This field is required</p>}
        </div>
        <div>
          <label htmlFor="postal">Postal code</label>
          <input type="text" id="postal" ref={postalInputRef}></input>
          {!postalIsValid && <p>5 digits is required</p>}
        </div>
      </div>
      <div className="form-actions">
        <button type="button" onClick={props.onClose}>
          Cancel
        </button>
        <button>Submit</button>
      </div>
    </form>
  );
};

export default Checkout;
